mapper.select:
SELECT s.fcode, s.fname, s.fdate, s.zspj, s.kpj, s.spj, sm.zdf, FORMAT((s.kpj - s.zspj), 2) kpzspcj, s.zdj, s.zgj, FORMAT((s.kpj - s.zspj), 2) zgzjcj, FORMAT((s.kpj - s.zspj) * 1000, 2) yqgztsy, FORMAT((s.kpj - s.zspj) * 5000, 2) wqgztsy, sm.fdate zjrq, sm.zljlrje FROM pc.stockdaypriceinfo s LEFT JOIN pc.stockmoneyflowinfo sm ON s.fcode = sm.fcode AND s.fdate = sm.fdate
AND s.fDate >= #{fdateq}
AND s.fDate <= #{fdatez}
AND s.fCode = #{fCode}
AND s.fName = #{fName}
ORDER BY s.fdate DESC
limit #{firstResult},#{maxResult}
